INSERT INTO `set_artifact` VALUES (1,'Искатель приключений.png','Искатель приключений',NULL,'Увеличивает макс. HP на 1000 ед.','В течение 5 сек. после открытия любого сундука восстанавливает 30% HP.'),(2,'Везунчик.png','Везунчик',NULL,'Увеличивает защиту на 100 ед.','Поднятие монет восстанавливает 300 HP.'),(3,'Целитель.png','Целитель',NULL,'Увеличивает получаемое Лечение на 20%.','Использование взрыва стихии восстанавливает 20% HP.'),(4,'Берсерк.png','Берсерк',NULL,'Увеличивает шанс крит. попадания на 12%.','Увеличивает шанс крит. попадания на 24% при HP ниже 70%.'),(5,'Решимость временщика.png','Решимость временщика',NULL,'Увеличивает силу атаки на 18%.','Увеличивает шанс крит. попадания заряженной атаки на 30%.'),(6,'Воин.png','Воин',NULL,'Увеличивает урон обычной и заряженной атаки на 15%.','Увеличивает урон обычной и заряженной атаки на 25% в течении 8 сек. после использования элементального навыка.'),(7,'Воля защитника.png','Воля защитника',NULL,'Увеличивает защиту на 30%.','Увеличивает сопротивление к элементам, отличающимся от элементов каждого вашего члена отряда, на 30%.'),(8,'Маленькое чудо.png','Маленькое чудо',NULL,'Увеличивает сопротивление ко всем элементам на 20%.','Получаемый элементальный урон повышает сопротивление к соответствующему элементу на 30% в течении 10 сек. Может возникнуть раз в 10 сек.'),(9,'Душа храбреца.png','Душа храбреца',NULL,'Увеличивает силу атаки на 18%.','Увеличивает на 30% урон против врагов с HP выше 50%.'),(10,'Азартный игрок.png','Азартный игрок',NULL,'Увеличивает урон элементальных навыков на 20%.','Победа над противником со 100% шансом восстанавливает время отката элементального навыка. Может возникнуть раз в 15 сек.'),(11,'Учёный.png','Учёный',NULL,'Увеличивает скорость восстановление энергии на 20%.','Получение элементальных сфер или частиц восстанавливает 3 ед. энергии членам отряда, экипированным стрелковым оружием или катализатором. Может возникнуть раз в 3 сек.'),(12,'Шаман молнии.png','Шаман молний',NULL,'Уменьшает длительность полученного Электро эффекта на 40%.',NULL),(13,'Шаман льда.png','Шаман льда',NULL,'Уменьшает длительность полученного Крио эффекта на 40%.',NULL),(14,'Шаман огня.png','Шаман огня',NULL,'Уменьшает длительность полученного Пиро эффекта на 40%.',NULL),(15,'Шаман воды.png','Шаман воды',NULL,'Уменьшает длительность полученного Гидро эффекта на 40%.',NULL),(16,'Инструктор.png','Инструктор',NULL,'Увеличивает мастерство стихий на 80 ед.','Вызов элементальной реакции увеличивает мастерство стихий всех членов отряда на 120 ед. в течении 8 сек.'),(17,'Изгнанник.png','Изгнанник',NULL,'Увеличивает скорость восстановления энергии на 20%.','Использование взрыва стихии восстанавливает 2 ед. энергии остальным членам отряда (но не выбранному персонажу) каждые 2 сек. в течение 6 сек. Эффект не может складываться.'),(18,'Конец гладиатора.png','Конец гладиатора',NULL,'Увеличивает силу атаки на 18%.','Увеличивает урон обычной атаки на 35%, если персонаж использует одноручное, двуручное или древковое оружие.'),(19,'Странствующий ансамбль.png','Странствующий ансамбль',NULL,'Увеличивает мастерство стихий на 80 ед.','Увеличивает урон заряженной атаки на 35%, если персонаж использует катализатор или стрелковое оружие.'),(20,'Возлюбленная юная дева.png','Возлюбленная юная дева','Долина воспоминаний','Увеличивает лечение персонажа на 15%.','Увеличивает получаемое лечение всех членов отряда на 20% в течении 10 сек. после использования элементального навыка или взрыва стихии.'),(21,'Церемония древней знати.png','Церемония древней знати','Чистая вода и горная пещера','Увеличивает урон взрыва стихии на 20%.','Активация взрыва стихии увеличивает силу атаки всех членов отряда на 20% в течении 12 сек. Эффект не складывается.'),(22,'Рыцарь крови.png','Рыцарь крови','Чистая вода и горная пещера','Увеличивает физ. урон на 25%.','После победы над противником увеличивает урон заряженной атаки на 50% и обнуляет стоимость выносливости заряженной атаки на 10 сек.'),(23,'Изумрудная тень.png','Изумрудная тень','Долина воспоминаний','Увеличивает бонус Анемо урона на 15%.','Увеличивает урон Рассеивания на 60%. При взаимодействии Рассеивания с другим элементом, также понижает сопротивление противника к соответствующему элементу на 40% в течении 10 сек.'),(24,'Громогласный рёв ярости.png','Громогласный рёв ярости','Июльские сады','Увеличивает бонус Электро урона на 15%.','Увеличивает урон реакций Перегрузка, Заряжен, Сверхпроводник и Вегетация на 40%. Урон от реакции Обострение увеличивается на 20%. При активации этих реакций, а также реакции Стимуляция, время отката элементального навыка уменьшается на 1 сек. Эффект может возникнуть не чаще 1 раза за 0.8 сек.'),(25,'Усмиряющий гром.png','Усмиряющий гром','Июльские сады','Повышает Электро сопротивление на +40%.','Увеличивает урон против врагов, находящихся под действием Электро, на 35%.'),(26,'Горящая алая ведьма.png','Горящая алая ведьма','Скрытый дворец Уван','Увеличивает бонус Пиро урона на 15%.','Увеличивает Урон статусов Перегрузка и Горение на 40%, а урон статусов Пар и Таяние - на 15%. Использование элементального навыка увеличивает эффекты набора из двух вещей на 50% в течении 10 сек. Может складываться до 3 раз.'),(27,'Ступающий по лаве.png','Ступающий по лаве','Скрытый дворец Уван','Пиро сопротивление +40%.','Увеличивает урон против врагов, находящихся под действием Пиро, на 35%.'),(28,'Архаичный камень.png','Архаичный камень','Владения Гуюнь','Увеличивает бонус Гео урона на 15%.','Подобранные осколки, образованные реакцией Кристалл, увеличивают бонус элементального урона всех членов отряда на 35% в течение 10 сек. Одновременно можно иметь бонус урона только одного элемента.'),(29,'Встречная комета.png','Встречная комета','Владения Гуюнь','Увеличивает прочность щита на 35%.','Под защитой щита увеличивает урон обычной и заряженной атаки на 40%.'),(30,'Заблудший в метели.png','Заблудший в метели','Пик Виндагнира','Увеличивает бонус Крио урона на 15%.','Атаки по противникам с эффектом Крио увеличивают шанс крит. попадания на 20%. Если противник имеет статус Заморозка, то дополнительно увеличивает шанс крит. попадания на 20%.'),(31,'Сердце глубин.png','Сердце глубин','Пик Виндагнира','Увеличивает бонус Гидро урона на 15%.','Увеличивает урон обычной и заряженной атаки на 30% в течение 15 сек. после использования элементального навыка.'),(32,'Стойкость Миллелита.png','Стойкость Миллелита','Горный дозор','Увеличивает макс. HP на 20%.','Когда элементальный навык попадает по врагу, атака всех членов отряда, которые находятся рядом, увеличивается на 20%, а прочность щитов увеличивается на 30% на 3 секунды. Эффект можно активировать каждых 0.5 секунд. Также эффект срабатывает даже в том случае, если использующий этот артефакт персонаж не находится на поле боя.'),(33,'Бледный огонь.png','Бледный огонь','Горный дозор','Увеличивает физ. урон на 25%.','Когда элементальный навык попадает по врагу, атака увеличивается на 9% в течение 7 секунд. Эффект может складываться до 2 раз и срабатывает каждые 0.3 секунды. Как только будет наложено 2 эффекта, бонусов 2 предметов увеличится до 100%.'),(34,'Эмблема рассечённой судьбы.png','Эмблема рассечённой судьбы','Кленовый зал','Увеличивает скорость восстановления энергии на 20%.','Увеличивает урон взрыва стихий на величину, равную 25% от значения восстановления энергии. Эффект можно увеличить максимум до 75%.'),(35,'Воспоминания Симэнавы.png','Воспоминания Симэнавы','Кленовый зал','Увеличивает силу атаки на 18%.','При использовании элементального навыка, если у персонажа 15 или более единиц энергии, он теряет 15 единиц энергии, а урон обычной атаки, заряженной атаки и атаки в падении увеличивается на 50% в течение 10 сек.'),(36,'Кокон сладких грёз.png','Кокон сладких грёз','Дремлющий сад','Увеличивает защиту на 30%.','Персонаж, экипированный этим набором артефактов, получает эффект Любопытства. 1 уровень Любопытства даётся активному персонажу после попадания по противнику атакой Гео (не чаще чем раз в 0,3 сек.). Когда персонаж находится вне поля боя, он получает 1 ур. Любопытства каждые 3 сек. Любопытство может складываться до 4 ур., каждый ур. прибавляет 6% к защите и 6% к Гео урону. Если персонаж не получает Любопытства в течение 6 сек., то теряет 1 накопленный уровень Любопытства.'),(37,'Моллюск морских красок.png','Моллюск морских красок','Дремлющий сад','Повышает бонус лечения на 15%.','Экипированный этим набором артефактов персонаж при лечении соратников создаёт Пузырь морских красок. Пузырь регистрирует восстановленные при лечении HP (в том числе избыточные - когда лечение превышает максимум здоровья). После окончания действия Пузырь взрывается и наносит окружающим врагам урон в размере 90% записанного объёма лечения (урон рассчитывается так же, как для эффектов Заряжен и Сверхпроводник, но на него не действуют бонусы мастерства стихий, уровня и реакций). Пузырь морских красок можно создавать не чаще, чем раз в 3,5 сек. Пузырь может записать до 30 000 восстановленных HP, в том числе HP избыточного лечения. В отряде не может существовать больше одного Пузыря морских красок одновременно. Этот эффект может быть активирован, даже если персонаж, экипированный набором артефактов, не находится на поле боя.'),(38,'Отголоски подношения.png','Отголоски подношения','Затерянная долина','Увеличивает силу атаки на 18%.','Попадания обычной атакой с 36% шансом активируют Ритуал долины: урон обычной атаки повышается на 70% от силы атаки. Через 0,05 сек. после нанесения урона обычной атакой эффект исчезает. Если обычной атаке не удалось активировать этот эффект, шанс его активации следующим попаданием возрастает на 20%. Шанс активации возникает не чаще одного раза в 0,2 сек.'),(39,'Киноварное загробье.png','Киноварное загробье','Затерянная долина','Увеличивает силу атаки на 18%.','После активации взрыва стихии на 16 сек. создаёт Скрытое сияние, которое повышает силу атаки на 8%. Сила атаки персонажа увеличивается ещё на 10%, когда он теряет HP. Этот эффект может возникнуть максимум 4 раза и не чаще, чем раз в 0,8 сек. Скрытое сияние исчезает, когда персонаж покидает поле боя. При повторной активации взрыва стихии существующее Скрытое сияние снимается.'),(40,'Воспоминания дремучего леса.png','Воспоминания дремучего леса','Шпиль одинокого просветления','Увеличивает бонус Дендро урона на 15%.','Попадание элементальным навыком или взрывом стихии снижает Дендро сопротивление врага на 30% на 8 сек. Этот эффект срабатывает, даже если экипированный персонаж не на поле боя.'),(41,'Позолоченные сны.png','Позолоченные сны','Шпиль одинокого просветления','Увеличивает мастерство стихий на 80 ед.','На 8 сек. после вызова элементальной реакции согласно элементам других персонажей в отряде экипированный персонаж получает следующие усиления:\\n· Сила атаки повышается на 14% за каждого персонажа с таким же элементом.\\n· Мастерство стихий повышается на 50 ед. за каждого персонажа с иным элементом. Каждое из этих усилений при подсчёте включает до 3 персонажей. Эффект может возникнуть один раз в 8 сек. и срабатывает, даже если экипированный персонаж не на поле боя.'),(42,'Хроники Чертогов в пустыне.png','Хроники Чертогов в пустыне','Песчаное царство','Увеличивает бонус Анемо урона на 15%.','Попадание заряженной атакой на 15 сек. повышает скорость обычной атаки на 10%, а урон обычной атаки, заряженной атаки и атаки в падении – на 40%.'),(43,'Цветок потерянного рая.png','Цветок потерянного рая','Песчаное царство','Увеличивает мастерство стихий на 80 ед.','Увеличивает урон реакций Бутонизация, Вегетация и Цветение экипированного этим набором персонажа на 40%. Кроме того, активация реакций Бутонизация, Вегетация и Цветение экипированным этим набором персонажем усилит этот эффект на 25%. Каждое суммирование длится 10 сек. Эффект складывается до 4 раз, может возникнуть раз в 1 сек. и активируется, даже если экипированный этим набором персонаж не активен.'),(44,'Сон нимфы.png','Сон нимфы','Заброшенная твердыня','Даёт 15% бонус Гидро урона.','Попадания обычной атаки, заряженной атаки, атаки в падении, элементального навыка и взрыва стихии по противнику на 8 сек. создают 1 уровень Отражения нимфы. Когда действуют 1/2/3 и более уровня Отражения нимфы, сила атаки повышается на 7%/16%/25%, а бонус Гидро урона на 4%/9%/15%. Уровни Отражения нимфы, созданные обычными атаками, заряженными атаками, атаками в падении, элементальными навыками и взрывами стихии, существуют независимо друг от друга.'),(45,'Сияние Вурукаши.png','Сияние Вурукаши','Заброшенная твердыня','Увеличивает НР на 20%.','Увеличивает урон элементального навыка и взрыва стихии на 10%. Если экипированный персонаж получает урон, этот эффект увеличивается на 80% на 5 сек. Эффект увеличения может складываться до 5 раз. Отсчёт времени каждого уровня происходит отдельно. Эти эффекты активируются даже если экипированный персонаж не на поле боя.'),(46,'Золотая труппа.png','Золотая труппа','Конец греха','Увеличивает урон элементального навыка на 20%.','Увеличивает урон элементального навыка на 25%. Кроме того, когда персонаж не на поле боя, урон элементального навыка дополнительно увеличивается на 25%. Через 2 сек. после вступления в бой эффект исчезает.'),(47,'Охотник Сумеречного двора.png','Охотник Сумеречного двора','Конец греха','Увеличивает урон обычной и заряженной атаки на 15%.','Увеличивает шанс крит. попадания на 12% на 5 сек., когда HP увеличивается или снижается. Эффект может складываться до 3 раз.'),(48,'Песнь былых времён.png','Песнь былых времён','Дробящееся собрание','Повышает бонус лечения на 15%.','Экипированный персонаж при лечении членов отряда на 6 сек. создаёт эффект Стремления, который регистрирует осуществлённое лечение (в том числе избыточное). С завершением длительности Стремление превращается в Волны былых времён: при попадании обычной атаки, заряженной атаки, атаки в падении, элементального навыка или взрыва стихии вашего активного персонажа по противнику наносимый урон увеличивается на 8% от общего количества лечения, зарегистрированного Стремлением. После 5 активаций или через 10 сек. эффект Волн тех времён исчезает. Однократный эффект Стремления регистрирует до 15 000 ед. HP. Одновременно может существовать только одно Стремление, но оно может регистрировать лечение, предоставляемое несколькими экипированными персонажами сразу. Этот эффект активируется, даже если экипированный персонаж не на поле боя.'),(49,'Ночной шёпот в Лесу откликающегося эха.png','Ночной шёпот в Лесу откликающегося эха','Дробящееся собрание','Увеличивает силу атаки на 18%.','После использования элементального навыка бонус Гео урона на 10 сек. увеличивается на 20%. Если персонаж находится под защитой щита, созданного реакцией Кристалл, этот эффект увеличивается на 150%. Через 1 сек. после потери щита данное дополнительное увеличение исчезает.');
